package chapter02;

public class PrintEscSec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// \n : 다음 행으로 이동.
		// \t : 다음 탭으로 이동.
		// \' : '을 출력.
		// \" : "을 출력.
		// \\ : \을 출력.
		// \b : 뒤로(왼쪽)으로 한칸 이동.
		// \r : 줄의 맨 앞으로 이동.
		System.out.print("\n줄바꿈\n연습\n\n\n");
		System.out.print("\tTap\t연습\n");
		System.out.print("인용부호\"KING\"입니다.\n");
		System.out.print("\\ \\ \\");
		System.out.println();
		System.out.print("안녕하세요");

	}

}
