package chapter02;

public class VarLongType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// long num = 100000000000;
		// The literal 100000000000 of type int is out of range
		long num = 100000000000L;

		System.out.println(num);

		// byte num2 = 300;
		// Type mismatch: cannot convert from int to byte

		// 정수형 데이터 저장 타입 : byte, short, int, long.
		// byte byteNum = 10; // 정수 타입의 변수 선언과 데이터 10을 저장.
		// short shortNum = 300; // 정수 타입의 변수 선언과 데이터 300을 저장.
		// int intNum = 100000; // 정수 타입의 변수 선언과 데이터 100000을 저장.
		// long longNum = 100000000000L; // 정수 타입의 변수 선언과 데이터 100000000000L을 저장.

	}

}
