package chapter03;

public class Operator03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 5;

		System.out.println("num1 : " + num1);

		int num2 = ++num1;

		System.out.println("num2 : " + num2);
		System.out.println("num1 : " + num1);

		System.out.println("++num1 : " + ++num1);

	}

}
