package chapter03;

public class Operator06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 30;
		int num2 = 2;
		float num3 = 7.0f;

		System.out.println(num1 + num2); // 30 + 2 = 32
		System.out.println(num1 - num2); // 30 - 2 = 28
		System.out.println(num1 * num2); // 30 * 2 = 60
		System.out.println(num1 / num2); // 30 / 2 = 15
		System.out.println(num1 / 7); // 30 / 7 = 4
		System.out.println(num1 / num3); // 30 / 7.0f = 4.285714
		System.out.println(num1 % num2);
		System.out.println(num1 % 7);
		System.out.println(num1 % num3);

	}

}
